                        public int compare(File file1, File file2) {
                            if (file1.isDirectory() && file2.isFile()) {
                                return -1;
                            } else if (file1.isFile() && file2.isDirectory()) {
                                return 1;
                            } else {
                                long size1 = file1.length();
                                long size2 = file2.length();
                                return Long.compare(size1, size2);
